/* ING-alert-message */function showAlertMessage(e){var t=$("<div/>").addClass("ING-messagebox").attr("id","additional-info-box").css("display","none").prependTo("body"),n=$("<div/>").addClass("container").appendTo(t),r=$("<ul/>").appendTo(n);for(var i in e.messages){var s=$("<li/>").appendTo(r),o=e.messages[i].text,u=$("<div/>").addClass("message").html("<span>"+o+"</span>");u.appendTo(s)}var a=$("<div/>").addClass("close-button").appendTo(t);a.on("click",function(){$.cookie(cookieName,JSON.stringify(e),{expires:expireDay});t.slideUp(function(){$(this).remove()})});t.slideDown()}var expiredDay=14,alertMessage={version:"20140324",messages:[{type:"alert",text:"Alert message : Internet Banking will be temporarily unavailable on 04 August 2013 between 00:30 hrs and 4:30 hrs due to planned maintenance. All other channels available as per normal."},{type:"alert",text:"Alert message: This is a sample one line message"}]},cookieName="ING-alert-message";if(typeof $.cookie(cookieName)=="undefined")showAlertMessage(alertMessage);else{var oldMessage=JSON.parse($.cookie(cookieName)),oldVersion=parseInt(oldMessage.version,10),newVersion=parseInt(alertMessage.version,10);newVersion>oldVersion&&showAlertMessage(alertMessage)};